INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Mass Effect Trilogy', 4.8, 2012, 'Role-playing (RPG), Shooter', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/1.png', 'Uma épica saga espacial repleta de escolhas e consequências.', 'Explore o universo como o Comandante Shepard, liderando uma equipe para salvar a galáxia de uma ameaça antiga. Cada decisão molda o destino da história e dos personagens.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Red Dead Redemption 2', 4.7, 2018, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/2.png', 'Uma história cativante de sobrevivência no Velho Oeste.', 'Viva como Arthur Morgan, um fora-da-lei tentando encontrar seu lugar em um mundo em mudança. Explore um mundo aberto vasto e cheio de detalhes, repleto de aventuras e perigos.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('The Witcher 3: Wild Hunt', 4.7, 2014, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/3.png', 'Uma jornada épica em um mundo de fantasia repleto de monstros e magia.', 'Jogue como Geralt de Rivia, um caçador de monstros em busca de sua filha adotiva em um mundo devastado pela guerra e repleto de escolhas morais complexas.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Sekiro: Shadows Die Twice', 3.8, 2019, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/4.png', 'Um desafio intenso em um Japão feudal reimaginado.', 'Assuma o papel de um shinobi em busca de vingança e redenção. Enfrente inimigos implacáveis em combates estratégicos e explore cenários cheios de segredos.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Ghost of Tsushima', 4.6, 2012, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/5.png', 'A luta pela liberdade em uma ilha devastada pela guerra.', 'Jogue como Jin Sakai, um samurai que deve abandonar suas tradições para proteger seu lar da invasão mongol. Uma experiência visualmente deslumbrante e emocionante.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Super Mario World', 4.7, 1990, 'Platform', 'Super Ness, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/6.png', 'Um clássico eterno da era dos 16 bits.', 'Acompanhe Mario e Luigi em sua aventura para salvar a Princesa Peach do maligno Bowser. Explore mundos vibrantes e descubra segredos inesquecíveis.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Hollow Knight', 4.6, 2017, 'Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/7.png', 'Uma exploração atmosférica em um mundo subterrâneo.', 'Descubra os mistérios de Hallownest, um reino em ruínas cheio de criaturas intrigantes e desafios que testarão sua habilidade e coragem.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Ori and the Blind Forest', 4, 2015, 'Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/8.png', 'Uma emocionante jornada de amizade e sacrifício.', 'Ajude Ori, um espírito guardião, a restaurar a luz de sua floresta em uma aventura visualmente impressionante e emocionalmente cativante.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Cuphead', 4.6, 2017, 'Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/9.png', 'Ação desenfreada com um estilo de animação clássico.', 'Enfrente chefões desafiadores e explore fases em um mundo inspirado nos desenhos animados dos anos 1930. Uma experiência única e nostálgica.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Sonic CD', 4, 1993, 'Platform', 'Sega CD, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/10.png', 'A velocidade de Sonic em uma aventura inovadora.', 'Viaje no tempo para salvar o futuro de Little Planet em uma das aventuras mais criativas do ouriço azul. Músicas memoráveis e jogabilidade clássica aguardam.');